"use strict";

const Matcher = require("../matcher.js");
const ValidationResult = require("../validation-result.js");
const Validator = require("../validator.js");

const PAGE_INFO_TYPE = `const pageInfoType = shape({
  pageSize: integer(1, null),
  pageIndex: integer(0, null),
  total: integer(-1, null)
});`;

class _PageInfo extends Matcher {

  constructor(...params) {
    super("pageInfo", ...params);
    this.validator = new Validator(PAGE_INFO_TYPE);
  }

  match(value, ctx) {
    let result = new ValidationResult();
    if (value == null) {
      return result;
    }

    let childResult = this.validator.validate("pageInfoType", value);
    if (!childResult.isValid()) {
      result.addInvalidValueError("pageInfo.e001", `The value must be pageInfo.`);
      Object.keys(childResult.childResults).forEach(childResultKey => {
        result.addChildResult(childResultKey, childResult.childResults[childResultKey]);
      })
    }

    return result;
  }

}

function PageInfo(...params) {
  let matcher = new _PageInfo(...params);
  if (this && this.matchers && this.matchers[0]) {
    this.matchers.push(matcher);
    return this;
  } else {
    let validator = new Validator(null);
    validator._loadMatchers();
    validator.matchers = [matcher];
    return validator;
  }
}

module.exports = PageInfo;
